import jsPDF from "jspdf";
import "jspdf-autotable";

export const generatePDF = (
  selectedOption,
  inputData,
  tableDataA,
  tableData,
  tableDataB
) => {
  const doc = new jsPDF();

  const logoScale = 0.8;
  const footerScale = 1.4;
  const xLeft = 15;
  const xRight = 140;

  // Calculate Total Amount
  let totalSumA = tableDataA.reduce(
    (acc, obj) => acc + Number(obj.quantity * obj.rate),
    0
  );
  let totalSum = tableData.reduce(
    (acc, obj) => acc + Number(obj.quantity * obj.rate),
    0
  );
  let totalFinalSum = totalSumA + totalSum;

  // Parsing Corporate Discount
  let corpDiscount;
  if (selectedOption === "QUOTE") {
    corpDiscount = inputData.input_6 / 100;
  } else if (selectedOption !== "PAYROLL") {
    corpDiscount = inputData.input_5 / 100;
  } else {
    corpDiscount = 0;
  }
  let corpDiscountAmt = totalFinalSum * corpDiscount;
  let discountedTotalFinalSum = totalFinalSum - corpDiscountAmt;

  // y-coordinate tracker
  let currY = 0;

  // MFTS logo
  let imgData = require("./MFTS_logo.png");
  doc.addImage(
    imgData,
    "PNG",
    65, // X coordinate of the image
    5, // Y coordinate of the image
    Math.trunc(96 * logoScale), // Width of the image
    Math.trunc(30 * logoScale) // Height of the image
  );

  // Header
  doc.setFont("helvetica");
  doc.setFontSize(9);

  if (selectedOption !== "PAYROLL") {
    // Add content to the PDF document using the input data
    doc.text(`To: ${inputData.input_0}`, xLeft, 35); // To
    doc.text(`Address: ${inputData.input_1}`, xLeft, 40); // Address
    doc.text(`Date: ${inputData.input_2}`, xRight, 35); // Date
    doc.text(`Quote/Invoice#: ${inputData.input_3}`, xRight, 40); // DocNum
  }

  // Document Type
  doc.setFontSize(14);
  if (selectedOption === "ACKRECEIPT") {
    doc.text(`ACKNOWLEDGEMENT RECEIPT`, 70, 50);
  } else if (selectedOption === "INV") {
    doc.text(`INVOICE`, 95, 50);
  } else if (selectedOption === "QUOTE") {
    doc.text(`QUOTATION`, 90, 50);
  } else if (selectedOption === "SOA") {
    doc.text(`STATEMENT OF ACCOUNT`, 70, 50);
  } else if (selectedOption === "PAYROLL") {
    doc.text(`PAYROLL`, 95, 40);
  }

  // Title of the Document. Set the text to center align
  let textWidth = 0;
  doc.setFontSize(11);
  if (selectedOption !== "PAYROLL") {
    textWidth = doc.getTextWidth(inputData.input_4);
    const pageWidth = doc.internal.pageSize.width;
    const centerX = (pageWidth - textWidth) / 2;
    doc.text(`${inputData.input_4}`, centerX, 57); // Add the centered text to the PDF document
    currY = 64;
  } else {
    textWidth = doc.getTextWidth(inputData.input_2); // for PAYROLL, title is index #2
    const pageWidth = doc.internal.pageSize.width;
    const centerX = (pageWidth - textWidth) / 2;
    doc.text(`${inputData.input_2}`, centerX, 42); // Add the centered text to the PDF document
    currY = 49;
  }

  // Particulars Table
  doc.setFontSize(10);
  if (tableDataA.length > 0) {
    doc.text(`PARTICULARS:`, xLeft, currY);
    currY += 2;
    doc.autoTable({
      startY: currY,
      columnStyles: {
        0: { columnWidth: 85 },
        1: { columnWidth: 12 },
        2: { columnWidth: 12 },
        3: { columnWidth: 23 },
        4: { columnWidth: 30 },
        5: { columnWidth: 20 },
      },
      headStyles: {
        halign: "center",
        fillColor: [120, 120, 120],
        fontSize: 8,
        lineColor: [0, 0, 0],
        lineWidth: 0.1,
      },
      footStyles: {
        halign: "left",
        fillColor: [190, 190, 190],
        textColor: [0, 0, 0],
        fontSize: 8,
        lineColor: [0, 0, 0],
        lineWidth: 0.1,
      },
      bodyStyles: {
        fontSize: 8,
        lineColor: [0, 0, 0],
        lineWidth: 0.1,
      },
      head: [["Description", "Qty", "Unit", "Rate", "Amount", "Remarks"]],
      body: tableDataA.map((row) => {
        const { description, quantity, unit, rate, remarks } = row;
        return [description, quantity, unit, rate, quantity * rate, remarks];
      }),
      // foot: [["Subtotal", "", "", "", `${totalSumA}   AED`, ""]],
      foot: [
        [
          "Subtotal",
          "",
          "",
          "",
          `${totalSumA.toLocaleString("en-US", {
            minimumFractionDigits: 2,
            maximumFractionDigits: 2,
          })}   AED`,
          "",
        ],
      ],
      theme: "striped",
    });
    currY = currY + tableDataA.length * 6 + 23;
  }

  // Materials Table
  if (tableData[0].description !== "") {
    doc.text(`MATERIALS:`, xLeft, currY);
    currY += 2;
    doc.autoTable({
      startY: currY,
      columnStyles: {
        0: { columnWidth: 85 },
        1: { columnWidth: 12 },
        2: { columnWidth: 12 },
        3: { columnWidth: 23 },
        4: { columnWidth: 30 },
        5: { columnWidth: 20 },
      },
      headStyles: {
        halign: "center",
        fillColor: [120, 120, 120],
        fontSize: 8,
        lineColor: [0, 0, 0],
        lineWidth: 0.1,
      },
      footStyles: {
        halign: "left",
        fillColor: [190, 190, 190],
        textColor: [0, 0, 0],
        fontSize: 8,
        lineColor: [0, 0, 0],
        lineWidth: 0.1,
      },
      bodyStyles: {
        fontSize: 8,
        lineColor: [0, 0, 0],
        lineWidth: 0.1,
      },
      head: [["Description", "Qty", "Unit", "Rate", "Amount", "Remarks"]],
      body: tableData.map((row) => {
        const { description, quantity, unit, rate, remarks } = row;
        return [description, quantity, unit, rate, quantity * rate, remarks];
      }),
      foot: [
        [
          "Subtotal",
          "",
          "",
          "",
          `${totalSum.toLocaleString("en-US", {
            minimumFractionDigits: 2,
            maximumFractionDigits: 2,
          })}   AED`,
          "",
        ],
      ],
      theme: "striped",
    });
    currY = currY + tableData.length * 6 + 20;
  }

  // Total row
  const totalrow = [
    [
      `TOTAL:`,
      `${totalFinalSum.toLocaleString("en-US", {
        minimumFractionDigits: 2,
        maximumFractionDigits: 2,
      })}   AED`,
    ],
  ];
  doc.autoTable({
    startY: currY,
    columnStyles: {
      0: { halign: "left" },
      1: { halign: "right" },
    },
    body: [totalrow[0]],
    theme: "striped",
  });

  // Corporate Discount row
  if (corpDiscount > 0) {
    const discRow = [
      [
        `Corporate Discount: ${corpDiscount * 100}%`,
        `(${corpDiscountAmt.toLocaleString("en-US", {
          minimumFractionDigits: 2,
          maximumFractionDigits: 2,
        })})   AED`,
      ],
    ];

    currY += 10;

    doc.autoTable({
      startY: currY,
      columnStyles: {
        0: { halign: "left" },
        1: { halign: "right" },
      },
      body: [discRow[0]],
      theme: "striped",
    });

    currY += 10;

    const discountedTotalRow = [
      [
        `TOTAL:`,
        `${discountedTotalFinalSum.toLocaleString("en-US", {
          minimumFractionDigits: 2,
          maximumFractionDigits: 2,
        })}   AED`,
      ],
    ];
    doc.autoTable({
      startY: currY,
      columnStyles: {
        0: { halign: "left" },
        1: { halign: "right" },
      },
      body: [discountedTotalRow[0]],
      theme: "striped",
    });

    totalFinalSum = discountedTotalFinalSum;
  }

  currY += 15;

  if (tableDataB[0].description !== "") {
    doc.text(`SCOPE OF WORK:`, xLeft, currY);
    currY += 2;
    doc.autoTable({
      startY: currY,
      bodyStyles: {
        fontSize: 8,
      },
      body: tableDataB.map((row) => {
        const { description, remarks } = row;
        return [description, remarks];
      }),
      theme: "striped",
    });
  }

  if (selectedOption === "QUOTE") {
    doc.setFontSize(11);
    doc.text(`Project Duration: ${inputData.input_5}`, xLeft, 200);
    doc.setFontSize(8);
    doc.text(`Terms and Agreement:`, xLeft, 208);
    if (inputData.input_8) {
      doc.text(
        `1. ${
          inputData.input_8
        }% downpayment upon confirmation of this quotation, and ${
          100 - inputData.input_8
        }% upon completion of the project. Work will not commence otherwise.`,
        xLeft,
        213
      );
    } else {
      doc.text(`1. No advance payment is required.`, xLeft, 213);
    }
    if (inputData.input_7) {
      // if Warranty field is populated
      doc.text(
        `2. Warranty is valid for ${inputData.input_7} starting from date of project commencement.`,
        xLeft,
        218
      );
    }
  }

  if (selectedOption !== "PAYROLL") {
    imgData = require("./Footer.png");
    doc.addImage(
      imgData,
      "PNG",
      xLeft - 3, // X coordinate of the image
      221, // Y coordinate of the image
      Math.trunc(122 * footerScale), // Width of the image
      Math.trunc(50 * footerScale) // Height of the image
    );
  }
  // Save the PDF document
  if (selectedOption === "QUOTE") {
    doc.save(
      `${inputData.input_3} - ${inputData.input_2} - ${inputData.input_0}.pdf`
    );
  } else if (selectedOption !== "PAYROLL") {
    doc.save(
      `${selectedOption} - ${inputData.input_3} - ${inputData.input_2} - ${inputData.input_0}.pdf`
    );
  } else {
    doc.save(`${inputData.input_1} - ${inputData.input_0}.pdf`);
  }
};
